import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import "./Profile.css";
import JSONPretty from 'react-json-pretty';
import AddRecipe from "./addRecipe";
import App from "../App";
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

import { Route, BrowserRouter as Router, Switch, Link } from "react-router-dom";
import slide01 from '../chefhat.png'

// export default class extends React.Component {
//   render() {
//     return (
      
//     );
//   }
// }

//Profile component
const Profile = () => {
  const { user, logout, isAuthenticated } = useAuth0();

  //If user is logged in, it displays user information

  return (
    isAuthenticated && (

     <div class = "container">
     <div class = "heading"><h1>Profile</h1></div>
     <div class="break"></div>
     <div><img className = "icon" src={user.picture} alt={user.name} /></div>
     <div class = "details">
      <div className= "username"><h2>{user.name}</h2></div>
      <div className= "email"><p>{user.email}</p></div>
      {/* <div className = "userdata"><JSONPretty data={user} /></div>
        {JSON.stringify(user, null, 2)} */}
           <div class="break"></div>
        <div class = "carousel">
      <CarouselProvider
              naturalSlideWidth={400}
              naturalSlideHeight={100}
              totalSlides={3}
            >
              <Slider>
                <Slide index={0}><img src={slide01}></img></Slide>
                <Slide index={1}><img src={slide01}></img></Slide>
                <Slide index={2}><img src={slide01}></img></Slide>
              </Slider>
              <ButtonBack type="button4" className="btn btn-warning btn-lg button left-button">Left</ButtonBack>
              <ButtonNext type="button4" className="btn btn-warning btn-lg button right-button">Right</ButtonNext>
            </CarouselProvider>
            </div>
      <div class = "button-container">
      <Link to="/addRecipe" name="hello" instructions="jee;;p">
      <button type="button1" className="btn btn-warning btn-lg button addrecipe-button" >Add Recipe</button>
                </Link>
      </div>
      <Link to="../App" name="hello" instructions="jee;;p">
      <button type="button2" className="btn btn-warning btn-lg button back-button" >Back</button>
        </Link>
      <button type="button3" className="btn btn-warning btn-lg button grocerylist-button" onClick={() => user()}>Grocery List</button>
      <button type="button4" className="btn btn-warning btn-lg button logout-button" onClick={() => logout()}>Log Out</button>

      </div>
      <Switch>
            <Route path="/addRecipe">
              <AddRecipe />
            </Route>
            <Route path="../App">
              <App />
            </Route>
          </Switch>
      </div>
    //grocery button doesn't work yet

    )

  )
}

export default Profile
