import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import "./LoginButton.css";
import image1 from '../30-Minute-Instant-Pot-Butter-Chicken-7.jpg'
import image2 from '../chefhat.png'
import Container from 'react-bootstrap/Container'

//Login button component

//If user is not logged in, it shows the login button
<Container>
  <Row>
    <Col>1 of 2</Col>
    <Col>2 of 2</Col>
  </Row>
</Container>

const LoginButton = () => {
  const { loginWithRedirect, isAuthenticated } = useAuth0();

  return ( 
    !isAuthenticated && (
      <>
      <div className="image1"><img src={image1} alt="Logo" /></div>
      <div className="image2"><img src={image2} alt="Logo2"/></div>
      <div className= "intro"><h1>Start cooking with PocketChef!</h1></div>
      <button type="button" className ="btn btn-warning btn-lg firstbutton" onClick={() => loginWithRedirect()}>
        Explore Recipes
      </button> 
     
      </>
    )
  )
}

export default LoginButton
