import React, { Component } from "react";
import ReactBootstrap, {
  Card,
  Button,
  Col,
  Grid,
  Panel,
  FormGroup,
} from "react-bootstrap";
import LikeButton from "./RatingButton.js";
import { Route, BrowserRouter as Router, Switch, Link } from "react-router-dom";
import editRecipe from "./EditRecipe";
import Modal from "react-modal";

Modal.setAppElement("#root");

const RecipeCard = (props) => {
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function closeModal() {
    setIsOpen(false);
  }

  function openModal() {
    setIsOpen(true);
  }

  return (
    <div>
      <Card style={{ width: "18rem" }}>
        <Card.Img variant='top' src={props.img} />
        <Card.Body>
          <Card.Title>{props.name}</Card.Title>
          <Card.Text>
            {props.instructions}
            <p></p>
            <h4>ingredients:</h4>
            {props.ingredients}
            <button onClick={openModal}>Open Modal</button>

            {/* edit recipe modal */}
            <Modal
              isOpen={modalIsOpen}
              onRequestClose={closeModal}
              contentLabel='Example Modal'
            >
              <form>
                <div className='addRecipeContainer'>
                  <label className='headerTitle'>Add New Recipe</label>

                  <label className='recipeTitle'>Image</label>
                  <img
                    className='recipeImg'
                    // src={this.state.img}
                    id='img-change'
                    alt='Loading...'
                  />
                  <input
                    type='text'
                    value={props.name}
                    className='recipeName'
                  ></input>

                  <label className='recipeTitle'>Name</label>
                  <input
                    type='text'
                    value={props.name}
                    // onChange={handleNameChange}
                    className='recipeName'
                  ></input>
                  <label className='recipeTitle'>Instructions</label>
                  <textarea
                    type='text'
                    value={props.instructions}
                    // onChange={handleInstructionsChange}
                    className='recipeInstructions'
                  ></textarea>
                  <label className='recipeTitle'>Ingredients</label>
                  <textarea
                    type='text'
                    value={props.instructions}
                    // onChange={handleInstructionsChange}
                    className='recipeInstructions'
                  ></textarea>
                  <div className='buttons'>
                    <button type='button' className='btn btn-danger btn-lg'>
                      Cancel
                    </button>
                    <button
                      type='button'
                      className='btn btn-success btn-lg'
                      //   onClick={this.submit}
                    >
                      Submit
                    </button>
                  </div>
                </div>
              </form>
            </Modal>
          </Card.Text>
          <LikeButton />
        </Card.Body>
      </Card>
    </div>
  );
};

export default RecipeCard;
