import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import "./Profile.css";
import JSONPretty from 'react-json-pretty';
import LogoutButton from './LogoutButton';


//Profile component
const Profile = () => {
  const { user, logout, isAuthenticated } = useAuth0();



  //If user is logged in, it displays user information
  return (
    isAuthenticated && ( 
    
     <div class = "container">
     <div><img className = "icon" src={user.picture} alt={user.name} /></div>
     <div className = "details">
      <div className= "username"><h2>{user.name}</h2></div>
      <div className= "email"><p>{user.email}</p></div>
      <div className = "userdata"><JSONPretty data={user} /></div>
        {/* {JSON.stringify(user, null, 2)} */}

      <div id="carouselExampleSlidesOnly" class="carousel slide" data-ride="carousel"> 
      <div class="carousel-inner">
        <div class="carousel-item active"> 
        <img class="d-block w-100" src="chefhat.png" alt="First slide"></img>
        </div> 
        <div class="carousel-item">
          <img class="d-block w-100" src="square.png" alt="Second slide"></img>
        </div>
        <div class="carousel-item">
          <img class="d-block w-100" src="square.png" alt="Third slide"></img>
        </div>
      </div>
      </div>
 

    
      <button type="button1" className="btn btn-warning btn-lg button back-button" onClick={() => user()}>Back</button>
      <button type="button2" className="btn btn-warning btn-lg button grocerylist-button" onClick={() => user()}>Grocery List</button> 
      <button type="button3" className="btn btn-warning btn-lg button logout-button" onClick={() => logout()}>Log Out</button> 

      </div>
      </div>
    //the back button and grocery button don't work yet

    )
    
  )
  $('#myCarousel').on('slide.bs.carousel', function () {
    $('.carousel').carousel({
      interval: 2000
    })
  })


}


export default Profile
