import React, { Component } from "react";
import "./recipe.css";

class Recipe extends Component {
  constructor() {
    super();
    this.state = {
      recipes: [],
    };
  }

  componentDidMount() {
    fetch("/getRecipes")
      .then((res) => res.json())
      .then((recipes) =>
        this.setState({ recipes }, () =>
          console.log("recipes fetched...", recipes)
        )
      );
  }


  render() {
    return (
      <div className="toggle">
        <div class="closed" id="container">
           {this.state.recipes.map((recipe) => (
        <h1>
            Recipe Name: {recipe.recipeName} 

        <p>ID: {recipe.idrecipe}</p>

            <ul class="ingredients">
          <p>  Instructions: {recipe.recipeInstruction}</p>
            </ul>

        </h1>
           ))}
    </div>
    </div>
    );

}
}

function byID(id) {
  return document.getElementById(id);
}

byID("toggle").onclick = function() {
  if (byID("container").classList.contains("closed")) {
    byID("container").classList.remove("closed");
  } else {
    byID("container").classList.add("closed");
  }
}
export default Recipe;
