import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import "./LoginButton.css";
import image from '../30-Minute-Instant-Pot-Butter-Chicken-7.jpg'

//Login button component

//If user is not logged in, it shows the login button
const LoginButton = () => {
  const { loginWithRedirect, isAuthenticated } = useAuth0();

  return ( 
    !isAuthenticated && (
      <>
      <div className="image"><img src={image} alt="Logo" /></div>
      <button type="button" className ="btn btn-warning btn-lg firstbutton" onClick={() => loginWithRedirect()}>
        Explore Recipes!
      </button> 
      </>
    )
  )
}

export default LoginButton
