import React, { Component } from "react";

function addRecipe(props) {
  if (props.location.recipeProps) {
    let name = props.location.recipeProps.name;
    let instructions = props.location.recipeProps.instructions;
    console.log(name, instructions);
  } else {
    let name = "Type recipe name here";
    let instructions = "Type recipe instructions here";
    console.log(name, instructions);
  }

  return (
    <form className='container'>
      <div>
        <label>Name</label>
        <input type='text' value={props}></input>
        <label>Instructions</label>
        <textarea type='text' value={props}></textarea>
        <button type='button' className='btn btn-danger btn-lg'>
          Cancel
        </button>
        <button type='button' className='btn btn-success btn-lg'>
          Submit
        </button>
      </div>
    </form>
  );
}

export default addRecipe;

