import React, { Component } from "react";
import "./addRecipe.css";
import Form from "react-bootstrap/Button";
import Button from "react-bootstrap/Button";
import add from "../assets/addImage.png";
import { Route, BrowserRouter as Router, Switch, Link } from "react-router-dom";

class addRecipe extends Component {
  //if the user is editing a current recipe, pass the name and intructions as props
  constructor(props) {
    super(props);
    this.state = {
      img: "https://static.thenounproject.com/png/187803-200.png",
      name: this.props.name,
      instructions: this.props.instructions,
    };
  }

  //change target name input field when user types
  handleNameChange = (event) => {
    this.setState({
      name: event.target.value,
    });
  };

  //change target instructions input field when user types
  handleInstructionsChange = (event) => {
    this.setState({
      instructions: event.target.value,
    });
  };

  handleImageChange = (event) => {
    this.setState({
      img: event.target.value,
    });
    console.log(this.state.img);
  };

  submit = (event) => {
    alert("Hello! I am an alert box!!");
  };

  render() {
    return (
      <form>
        <div className='addRecipeContainer'>
          <label className='recipeTitle'>Image</label>
          <img
            className='recipeImg'
            src={this.state.img}
            id='img-change'
            alt='Loading...'
          />
          <input
            type='text'
            value={this.props.name}
            onChange={this.handleImageChange}
            className='recipeName'
          ></input>

          <label className='recipeTitle'>Name</label>
          <input
            type='text'
            value={this.props.name}
            onChange={this.handleNameChange}
            className='recipeName'
          ></input>
          <label className='recipeTitle'>Instructions</label>
          <textarea
            type='text'
            value={this.props.instructions}
            onChange={this.handleInstructionsChange}
            className='recipeInstructions'
          ></textarea>
          <label className='recipeTitle'>Ingredients</label>
          <textarea
            type='text'
            value={this.props.instructions}
            onChange={this.handleInstructionsChange}
            className='recipeInstructions'
          ></textarea>
          <div className='buttons'>
            <button
              type='button'
              className='btn btn-danger btn-lg'
              onClick={this.submit}
            >
              Cancel
            </button>
            <button
              type='button'
              className='btn btn-success btn-lg'
              onClick={this.submit}
            >
              Submit
            </button>
          </div>
        </div>
      </form>
    );
  }
}

export default addRecipe;
