import React, { Component } from "react";
import "./addRecipe.css";
import Form from "react-bootstrap/Button";
import Button from "react-bootstrap/Button";
import add from "../assets/addImage.png";
class addRecipe extends Component {
  //if the user is editing a current recipe, pass the name and intructions as props
  constructor(props) {
    super(props);
    this.state = {
      img: "https://static.thenounproject.com/png/187803-200.png",
      name: this.props.name,
      instructions: this.props.instructions,
    };
  }

  //change target name input field when user types
  handleNameChange = (event) => {
    this.setState({
      name: event.target.value,
    });
  };

  //change target instructions input field when user types
  handleInstructionsChange = (event) => {
    this.setState({
      instructions: event.target.value,
    });
  };

  render() {
    return (
      <form>
        <div className='addRecipeContainer'>
          <img src={img} alt='new' />
          <label>Name</label>
          <input
            type='text'
            value={this.props.name}
            onChange={this.handleNameChange}
            className='recipeName'
          ></input>
          <label>Instructions</label>
          <textarea
            type='text'
            value={this.props.instructions}
            onChange={this.handleInstructionsChange}
            className='recipeInstructions'
          ></textarea>
          <label>Ingredients</label>
          <textarea
            type='text'
            value={this.props.instructions}
            onChange={this.handleInstructionsChange}
            className='recipeInstructions'
          ></textarea>
          <div className='buttons'>
            <button type='button' className='btn btn-danger btn-lg'>
              Cancel
            </button>
            <button type='button' className='btn btn-success btn-lg'>
              Submit
            </button>
          </div>
        </div>
      </form>
    );
  }
}

export default addRecipe;
