import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import "./LoginButton.css";
import image1 from '../30-Minute-Instant-Pot-Butter-Chicken-7.jpg'
import image2 from '../chefhat.png'

//Login button component
//If user is not logged in, it shows the login button

const LoginButton = () => {
  const { loginWithRedirect, isAuthenticated } = useAuth0();

  return ( 
    !isAuthenticated && (
      <>
      <div class = "grid-container">
        <div class="grid-item" className="image1"><img src={image1}/></div>
        <div class="grid-item" className="image2"><img src={image2}/></div>
        <div class="grid-item" className= "intro"><h1>Start cooking with PocketChef!</h1></div>
        <button type="button" class="grid-item" className="btn btn-warning btn-lg firstbutton" onClick={() => loginWithRedirect()}>
        Explore Recipes
        </button>
        
      </div>
     
      </>
    )
  )
}

export default LoginButton
